function timeCalc(){var t=(new Date).getTime(),e=new Date(2013,8,25,0,3,10).getTime(),n=Math.abs(Math.floor((t-e)/1e3)),a=calculateUnit(n,86400),i=calculateUnit(n-86400*a,3600),l=calculateUnit(n-86400*a-3600*i,60),c=calculateUnit(n-86400*a-3600*i-60*l,1),r=i+":"+(10>l?"0"+l:l)+":"+(10>c?"0"+c:c);return timePrint(n,a,r)}function calculateUnit(t,e){var n=Math.abs(n=t/e)<1?0:n;return Math.abs(0>n?Math.ceil(n):Math.floor(n))}function timePrint(t,e,n){t>518400?(document.getElementById("time").innerHTML=e,document.getElementById("tStr").innerHTML="days left"):(document.getElementById("time").innerHTML=n,document.getElementById("tStr").innerHTML="hours left")}var counter=setInterval(timeCalc,1e3);